 local!notePagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 10, sort: #"SYSTEM_SYSRULES_sortInfo"(field: "username", ascending: true)),

 with(
 local!attendeeDatasubset: todatasubset(rf!attendees,local!attendeePagingInfo),
