          value: rf!meeting.subject,
          readOnly: true
        ),
        #"SYSTEM_SYSRULES_paragraphField"(
          label: "Description",
          labelPosition: "ABOVE",
          value: rf!meeting.description,
          readOnly: true
        ),
        #"SYSTEM_SYSRULES_textField"(
          label: "Location",
          labelPosition: "ABOVE", 
          value: rf!meeting.location,
          readOnly: true
        )
      },
      secondColumnContents: {
        #"SYSTEM_SYSRULES_dateTimeField"(
          label: "Start Time",
          labelPosition: "ABOVE",          
          value: rf!meeting.startDateTime,
          readOnly: true
        ),
