          value: rf!meeting.durationMinutes,
          readOnly: true
        ),
        #"SYSTEM_SYSRULES_textField"(
          label: "Scheduled By",
          labelPosition: "ABOVE", 
          value: #"_a-0000d690-12fb-8000-0f1a-010000010000_1352"(rf!meeting.creatorUsername),
          readOnly: true
        )
      }
    ),
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Attendees",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_gridField"(
          totalCount: local!attendeeDatasubset.totalCount,
          columns: {
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Attendee Name",
              field: "username",
              data: apply(#"_a-0000d690-12fb-8000-0f1a-010000010000_1352",local!attendeeDatasubset.data.username)
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Checked In?",
              field: "checkedIn",
              data: if(index(local!attendeeDatasubset.data, "checkedIn", {}), "Yes", "No")
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Check-In Time",
              field: "checkInDateTime",
              data: index(local!attendeeDatasubset.data, "checkInDateTime", null)
            )
          },
          value: local!attendeePagingInfo,
          saveInto: local!attendeePagingInfo
        )
      }
    ),
    if(#"_a-0000d62a-67b3-8000-da6f-010000010000_951"({rf!allNotes}),
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Notes",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_textField"(
          label: "",
          value: "Attendees have not added any notes for this meeting.",
          readOnly: true
        )
      }
    ),
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Notes",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_gridField"(
          totalCount: local!noteDatasubset.totalCount,
          columns: {
             #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Meeting Note",
              field: "notes",
              data: index(local!noteDatasubset.data, "notes", null)
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Written By",
              field: "username",
              data: apply(#"_a-0000d690-12fb-8000-0f1a-010000010000_1352",index(local!noteDatasubset.data, "username", null))
            )
          },
          value: local!notePagingInfo,
          saveInto: local!notePagingInfo
        )
      }
    )
    )
  }
 )
 )
)
