rule.definition:
=
load(
 local!reminder: 'type!{https://tcstemp.appiancloud.com/suite/types/}MMS_Reminders'(),
 local!reminderPagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 5, sort: #"SYSTEM_SYSRULES_sortInfo"(field: "reminderDateTime", ascending: true)),

 with(
  local!reminderDatasubset: todatasubset(ri!reminders,local!reminderPagingInfo),

 #"SYSTEM_SYSRULES_formLayout"(
  label: "Add Meeting Reminder",
  instructions: "Complete the reminder details information below. Click 'Add Another Reminder' to add another meeting reminder. Click 'Submit' to save the reminder(s) and finish creating the meeting. Click 'Cancel' to finish creating the meeting without saving the reminder(s).",
  firstColumnContents: {
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Meeting Details",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_textField"(
          label: "Subject",
          labelPosition: "ABOVE",
          value: ri!meeting.subject,
          readOnly: true
        ),
        #"SYSTEM_SYSRULES_paragraphField"(
          label: "Description",
          labelPosition: "ABOVE",
          value: ri!meeting.description,
          readOnly: true
        ),
        #"SYSTEM_SYSRULES_textField"(
          label: "Location",
          labelPosition: "ABOVE", 
          value: ri!meeting.location,
          readOnly: true
        )
      },
      secondColumnContents: {
        #"SYSTEM_SYSRULES_dateTimeField"(
          label: "Start Time",
          labelPosition: "ABOVE",          
          value: ri!meeting.startDateTime,
          readOnly: true
        ),
        #"SYSTEM_SYSRULES_integerField"(
          label: "Duration (minutes)",
          labelPosition: "ABOVE",          
          value: ri!meeting.durationMinutes,
          readOnly: true
        )
      }
    ),
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Reminder Details",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_dateTimeField"(
          label: "Time",
          labelPosition: "ABOVE",
          value: local!reminder.reminderDateTime,
          saveInto: local!reminder.reminderDateTime,
          required: true,
          validations: 
            if(local!reminder.reminderDateTime<now(),"The reminder may not be scheduled in the past",
              if(local!reminder.reminderDateTime>ri!meeting.startDateTime,"The reminder must be before the start of the meeting",null)
            )
        )
      },
      secondColumnContents: {
        #"SYSTEM_SYSRULES_radioButtonField"(
          label: "Would you like to review the reminder before sending?",
          labelPosition: "ABOVE",
          choiceLabels: {"Yes", "No"},
          choiceValues: {true(), false()},
          value: local!reminder.reviewBeforeSending,
          saveInto: local!reminder.reviewBeforeSending,
          required: true
        )
      }
    ),
    if(#"_a-0000d62a-67b3-8000-da6f-010000010000_951"({ri!reminders.reminderDateTime}),
    {},
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_gridField"(
          label: "Reminders to Schedule",
          totalCount: local!reminderDatasubset.totalCount,
          columns: {
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Time",
              field: "reminderDateTime",
              data: index(local!reminderDatasubset.data, "reminderDateTime", {})
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Review?",
              field: "reviewBeforeSending",
              data: if(index(local!reminderDatasubset.data, "reviewBeforeSending", null), "Yes", "No")
            )
          },
          value: local!reminderPagingInfo,
          saveInto: local!reminderPagingInfo
        )
      }
    )
    )
  },
  buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
    primaryButtons: {
      #"SYSTEM_SYSRULES_ButtonWidget"(
        label: "Add Another Reminder",
        style: "NORMAL",
        value: local!reminder,
        saveInto: {
          a!save(ri!reminders, if(#"_a-0000d62a-67b3-8000-da6f-010000010000_951"({ri!reminders.reminderDateTime}),save!value,append(ri!reminders,save!value))),
          a!save(local!reminder, 'type!{https://tcstemp.appiancloud.com/suite/types/}MMS_Reminders'())
        },
        validate: true
      ),
      #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
        label: "Submit",
        style: "PRIMARY",
        value: local!reminder,
        saveInto: a!save(ri!reminders, if(#"_a-0000d62a-67b3-8000-da6f-010000010000_951"({ri!reminders.reminderDateTime}),save!value,append(ri!reminders,save!value)))
      )
    },
    secondaryButtons: {
      #"SYSTEM_SYSRULES_ButtonWidgetSubmit"(
        label: "Cancel",
        style: "NORMAL",
        skipValidation: true
      )
    }
  )
 )
 )
)
