rule.definition:
load(
  local!user,
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 20,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "creatorUsername",
      ascending: true
    )
  ),
  with(
    local!datasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000dac9-871e-8000-9ba2-011c48011c48_37729",
      query: #"SYSTEM_SYSRULES_query"(
        aggregation: #"SYSTEM_SYSRULES_queryAggregation"(aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "creatorUsername", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "postMeetingRating", aggregationFunction: "AVG")
        }),
        filter:
          if(#"_a-0000d62a-67b3-8000-da6f-010000010000_951"({local!user}),
          null,
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "creatorUsername",
            operator: "includes",
            value: tostring(local!user[1])
          )
          ),
        pagingInfo: local!pagingInfo
      )
    ),
    #"SYSTEM_SYSRULES_dashboardLayout"(
      firstColumnContents:{
        #"SYSTEM_SYSRULES_sectionLayout"(
          firstColumnContents:{
            #"SYSTEM_SYSRULES_pickerFieldUsers"(
              label: "Meeting Creator",
              instructions: "Type the name of a user to filter the data by meeting creator",
              maxSelections: 1,
              groupFilter: #"_a-0000dac9-871e-8000-9ba2-011c48011c48_37733",
              value: local!user,
              saveInto: {
                local!user,
                a!save(local!pagingInfo.startIndex, 1)
              }
            )
          },
          secondColumnContents:{
            #"SYSTEM_SYSRULES_textField"(
              readOnly:true
            )
          }
        ),
        #"SYSTEM_SYSRULES_sectionLayout"(
          firstColumnContents:{
            if(local!datasubset.totalCount>0,
            
              #"SYSTEM_SYSRULES_gridField"(
              totalCount: local!datasubset.totalCount,
              columns: {
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Meeting Creator",
                  field: "creatorUsername",
                  data: apply(#"_a-0000d690-12fb-8000-0f1a-010000010000_1352",index(local!datasubset.data, "creatorUsername", null))
                ),
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Average Usefulness Rating",
                  field: "postMeetingRating",
                  data: index(local!datasubset.data, "postMeetingRating", null)
                )
              },
              value: local!pagingInfo,
              saveInto: local!pagingInfo
            )
            ,
            #"SYSTEM_SYSRULES_textField"(
            value:"No Records Found",
            readOnly:true
            )
            )
          }
        )
      }
    )
  )
)
