rule.definition:
load(
  local!chartPagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: -1,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "createMonthGrouped",
      ascending: true
    )
  ),
   local!gridPagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 20,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
      field: "createdDate",
      ascending: true
    )
  ),
  local!selectedMonth,
  local!selectedDateRange: 12,
  with(
    local!chartDatasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000dac9-871e-8000-9ba2-011c48011c48_37728",
      query: #"SYSTEM_SYSRULES_query"(
        aggregation: #"SYSTEM_SYSRULES_queryAggregation"(aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "createMonth", alias: "createMonthGrouped", isGrouping: true),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(field: "meetingId", aggregationFunction: "COUNT"),
        }
        ),
        filter: #"SYSTEM_SYSRULES_queryFilter"(field: "createdDate", operator: ">=", value: edate(today(),-local!selectedDateRange)),
        pagingInfo: local!chartPagingInfo
      )
    ),
    local!gridDatasubset: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000dac9-871e-8000-9ba2-011c48011c48_37728",
      query: #"SYSTEM_SYSRULES_query"(
        selection: #"SYSTEM_SYSRULES_querySelection"(columns: {
			        #"SYSTEM_SYSRULES_queryColumn"(field: "creatorUsername"),
              #"SYSTEM_SYSRULES_queryColumn"(field: "subject"),
              #"SYSTEM_SYSRULES_queryColumn"(field: "createdDate")
        }),
        filter: if(
          isnull(local!selectedMonth),
          null,
          #"SYSTEM_SYSRULES_queryFilter"(field: "createMonth", operator: "=", value: local!selectedMonth)
        ),
        pagingInfo: local!gridPagingInfo
      )
    ),
    #"SYSTEM_SYSRULES_dashboardLayout"(
      firstColumnContents:{
       #"SYSTEM_SYSRULES_sectionLayout"(
         firstColumnContents:{
       #"SYSTEM_SYSRULES_dropdownField"(
        label: "Filter Date Range",
        instructions: "Select a number of months to filter the report",
        choiceLabels: {"Last 3 Months", "Last 6 Months", "Last 9 Months", "Last 12 Months"},
        choiceValues: {3,6,9,12},
        value: local!selectedDateRange,
        saveInto: local!selectedDateRange,
        required: true
      )
      },
      secondColumnContents:{
        #"SYSTEM_SYSRULES_textField"(
          readOnly:true
        )
      }
      ),
    #"SYSTEM_SYSRULES_pieChartField"(
      series: {
        apply(
          #"SYSTEM_SYSRULES_chartSeries"(label: _, data: _,links: _),
          merge(
            index(local!chartDatasubset.data, "createMonthGrouped", {}),
            index(local!chartDatasubset.data, "meetingId", {}),
            apply(
            #"8a6ca824-5c2e-47e6-a57a-6ee329b2045c"(
                  monthValue: _,
                  selectedMonth: local!selectedMonth,
                  pagingInfo: local!gridPagingInfo
                ),
                index(local!chartDatasubset.data, "createMonthGrouped", {})
              )
          )
        )
      },
      showDataLabels: true,
      showTooltips: true
    ),
    #"SYSTEM_SYSRULES_gridField"(
			label: "Meetings",
      totalCount: local!gridDatasubset.totalCount,
			columns: {
			  #"SYSTEM_SYSRULES_gridTextColumn"(label: "Date Created", field: "createdDate", data: index(local!gridDatasubset.data, "createdDate", {})),
				#"SYSTEM_SYSRULES_gridTextColumn"(label: "Creator", field: "creatorUsername", data: apply(#"_a-0000d690-12fb-8000-0f1a-010000010000_1352",index(local!gridDatasubset.data, "creatorUsername", {}))),
				#"SYSTEM_SYSRULES_gridTextColumn"(label: "Subject", field: "subject", data: index(local!gridDatasubset.data, "subject", {}))
        },
        value: local!gridPagingInfo,
        saveInto: local!gridPagingInfo
    )
    }
    )
  )
)
